export interface CacheEntry {
  changed: boolean;
  cacheKey: string;
}

export interface IDataVersion {
  id: number;
  version: number;
  dataType: number;
}

export interface Config {
  configId: number;
  appId: number;
  config: string;
  'type': number;
  version: number;
}

export interface Group extends CacheEntry, IDataVersion {
  name: string;
  description: string;
}

export interface Site extends CacheEntry, IDataVersion {
  name: string;
  hosts: string [];
  groupId: number;
  description: string;
}

export interface Locale extends CacheEntry, IDataVersion {
  name: string;
  description: string;
}

export interface Currency extends CacheEntry, IDataVersion {
  name: string;
  scale: number;
}

export interface Channel extends CacheEntry, IDataVersion {
  name: string;
  alias: string;
}

export interface Source extends CacheEntry, IDataVersion {
  name: string;
}

export interface Key extends CacheEntry, IDataVersion {
  name: string;
  appId: string;
}

export interface Data extends CacheEntry, IDataVersion {
  keyId: number;
  groupId?: number;
  siteId?: number;
  currencyId?: number;
  channelId?: number;
  localeId?: number;
  sourceId?: number;
  other?: string;
  appId: string;
  value: string;
  description: string;
}

export interface ExtendedData extends Data {
  key: Key;
  group?: Group;
  site?: Site;
  currency?: Currency;
  channel?: Channel;
  locale?: Locale;
  source?: Source;
}

export interface CurrencySwitch extends CacheEntry {
  switchId: number;
  site: string;
  group: string;
  locale: string;
  channel: string;
  currency: string;
  source: string;
  value: boolean;
}
