'use strict'
const is = require('is-type-of')
const Foundation = require('../../common/foundation')
const CacheConfig = require('../cache-config')
const ConfigType = require('../../common/config-type')
const DataType = require('../../common/data-type')
const CacheEntry = require('../base-cache-entry')
const generateCacheKey = require('../method/generate-cache-key')
const AbstractCache = require('./abstract-cache')
const soaClient = require('../../service/soa-client')

class KeyCache extends AbstractCache {
  constructor () {
    super(
      CacheConfig.ClientCacheName.KEY,
      ConfigType.KEY
    )
  }

  load () {
    if (this.isFirstLoad) {
      this.isFirstLoad = false
      return soaClient.getAppKeyData(
        Foundation.appId,
        CacheConfig.DEFAULT_VERSION,
        true
      )
    }
    return soaClient.getAppKeyData(
      Foundation.appId,
      this.getCacheVersion(),
      false
    )
  }

  convert (data) {
    if (!is.object(data)) {
      return null
    }
    const key = new CacheEntry(
      generateCacheKey(data.name + data.appId, CacheConfig.KeyType.KEY),
      DataType.APP_KEY
    )
    Object.assign(key, data)
    return key
  }

  isNeededUpdate (previous, current) {
    return true
  }
}

module.exports = KeyCache
