'use strict'
const is = require('is-type-of')
const CacheConfig = require('../cache-config')
const ConfigType = require('../../common/config-type')
const DataType = require('../../common/data-type')
const CacheEntry = require('../base-cache-entry')
const generateCacheKey = require('../method/generate-cache-key')
const AbstractCache = require('./abstract-cache')
const soaClient = require('../../service/soa-client')

class SwitchCache extends AbstractCache {
  constructor () {
    super(
      CacheConfig.ClientCacheName.SWITCH,
      ConfigType.SWITCH
    )
  }

  load () {
    if (this.isFirstLoad) {
      this.isFirstLoad = false
      return soaClient.getCurrencySwitch(true)
    }
    return soaClient.getCurrencySwitch(false)
  }

  convert (data) {
    if (!is.object(data)) {
      return null
    }
    const key = new CacheEntry(
      generateCacheKey(data.switchId, CacheConfig.KeyType.SWITCH),
      DataType.SWITCH
    )
    Object.assign(key, data)
    return key
  }

  isNeededUpdate (previous, current) {
    return true
  }
}

module.exports = SwitchCache
