'use strict'
const logging = require('@ctrip/node-vampire-clogging')
const Logger = require('../logger')

class CLogger extends Logger {
  get enabledEnv () {
    return ['fws', 'uat', 'lpt', 'prod']
  }

  set enabledEnv (arg) {
    throw new Error('readonly property')
  }

  info (args) {
    logging.info('[cargo-3.0]', ...args)
  }

  warning (args) {
    logging.warn('[cargo-3.0]', ...args)
  }

  error (args) {
    logging.error('[cargo-3.0]', ...args)
  }
}

module.exports = new CLogger()
