const log = require('../log')

// check mock-mode
let isMock = false
try {
  if (process.env.NODE_ENV === 'cargo_mock') {
    log.info('service', 'mock mode')
    isMock = true
  }
} catch (e) {
  isMock = false
}

module.exports = isMock
  ? require('./mock-client')
  : require('./service-client')
