'use strict'
const assert = require('assert')

/**
 * @param {import('../index')} cargo
 */
module.exports = (cargo) => {

  it('TopCurrencies.ctrip.en === 21,13,22,9,16', () => {
    assert.strictEqual(
      cargo.select('TopCurrencies').system().group('ctrip').site('en').query(),
      '21,13,22,9,16'
    )
  })

  it('TopCurrencies.trip.hk=== 22,18,13,21', () => {
    assert.strictEqual(
      cargo.select('TopCurrencies').system().group('trip').site('hk').query(),
      '22,18,13,21'
    )
  })

  it('TopCurrencies.trip === ""', () => {
    assert.strictEqual(
      cargo.select('TopCurrencies').system().group('trip').query(),
      ''
    )
  })

  it('TopCurrencies.trip.invalid === ""', () => {
    assert.strictEqual(
      cargo.select('TopCurrencies').system().site('trip').other('invalid').query(),
      ''
    )
  })

  it('TopCurrencies.trip.invalid === ""', () => {
    assert.strictEqual(
      cargo.select('TopCurrencies').system().site('trip').other('invalid').query(),
      ''
    )
  })

  it('SupportedLocales.Ctrip.EN === 1', () => {
    assert.strictEqual(
      cargo.select('SupportedLocales').system().group('ctrip').site('EN').query(),
      '1'
    )
  })

  it('SupportedLocales.Trip.HK === 8,10', () => {
    assert.strictEqual(
      cargo.select('SupportedLocales').system().group('trip').site('hk').query(),
      '8,10'
    )
    assert.strictEqual(
      cargo.select('SupportedLocales').appid('999999999').group('trip').site('hk').query(),
      '8,10'
    )
    assert.strictEqual(
      cargo.select('SupportedLocales').appid('12345').group('trip').site('hk').query(),
      ''
    )
  })

  it('TopCurrencies.QueryList', () => {
    assert.strictEqual(
      cargo.select('TopCurrencies').system().queryList().length,
      36
    )
  })

  it('TopCurrencies.Trip.QueryList', () => {
    assert.strictEqual(
      cargo.select('TopCurrencies').system().group('trip').queryList().length,
      23
    )
  })

  it('TopCurrencies.Ctrip.QueryList', () => {
    assert.strictEqual(
      cargo.select('TopCurrencies').system().group('ctrip').queryList().length,
      12
    )
  })

  it('TopCurrencies.SC.QueryList', () => {
    assert.strictEqual(
      cargo.select('TopCurrencies').system().group('sc').queryList().length,
      1
    )
    assert.strictEqual(
      cargo.select('TopCurrencies').appid('999999999').group('sc').queryList().length,
      1
    )
    assert.strictEqual(
      cargo.select('TopCurrencies').appid('12345').group('sc').queryList().length,
      0
    )
  })
}
