const pkgInfo = require('@ctrip/node-vampire-pkginfo');

let versionsPromise = new Promise(function(resolve) {
    pkgInfo.getInfo({
        'startsWith': ['ctriputil', '@ctrip/']
    }, resolve);
});

module.exports = {
    getVersions: function(callback) {
        if (callback) {
            versionsPromise.then(callback);
        }
    }
};