let versionString = process.version
  .replace(/(\d+)/g, (t) => t.padStart(3, "0"))
  .replace(/[^\d]/g, "");

let ACT_MINI_VERSION = "016014000";

let cat = null;
if (process.env.USE_BAT != "false") {
  console.log("use bat");
  cat = require("@ctrip/node-vampire-bat");
} else {
  cat = versionString >= ACT_MINI_VERSION
    ? require("./catModules_v16/index")
    : require("@ctrip/node-vampire-cat-v14");
}

module.exports = cat;