import { tripLogMessage } from '@ctrip/node-vampire-triplog'

interface _Clogging {
    debug(...args: any[]): void;

    /**clogging 对外接口方法 info */
    info(...args: any[]): void;

    /**clogging 对外接口方法 warn */
    warn(...args: any[]): void;

    /**clogging 对外接口方法 error */
    error(...args: any[]): void;

    /**clogging 对外接口方法 fatal */
    fatal(...args: any[]): void;

    /**clogging 对外接口方法 custom（传入type） */
    custom(data: tripLogMessage): void;

    setCollectStdio(collectStdio?: boolean): void;
}

export const Clogging: _Clogging;
export default Clogging;
