import { IncomingMessage, ServerResponse } from "http";

export interface foundationFramework {
    getAppID(): string;

    getEnv(): Promise<string>;

    getSubEnv(): Promise<string>;

    getIDC(): Promise<string>;

    getHostIP(): string;

    getServerProperties<T>(key: string | '*'): Promise<T> | undefined;

    getServerPropertiesSync(key?: string): object | string;

    getConfig<T>(key: string | '*'): Promise<T> | undefined;

    getClientIp(req: IncomingMessage): string;

    isIP(value: string): boolean;

    isIPv4(value: string): boolean;

    isIPv6(value: string): boolean;

    toIPv6SimpleAddress(value: string): string;

    toIPv6FullAddress(value: string): string;

}

declare const _foundationFramework: foundationFramework;

export default _foundationFramework

