import http from 'http';

interface HttpRequestOptions extends http.RequestOptions {
  contentType?: ContentType;
  rejectUnauthorized?: boolean;
  responseTimeout?: number;
  body?: RequestBody;
  unref?: boolean;
  time?: boolean;
  proxy?: boolean;
  gzip?: boolean;
}

type ContentType =
  | 'text'
  | 'json'
  | 'statusCode'
  | 'response'
  | 'buffer'
  | 'uint8array'
  | 'binary';

type RequestBody = string | Buffer;

export default function SendRequest<T>(
  reqUrl: string,
  options?: HttpRequestOptions | RequestBody,
  postData?: RequestBody
): Promise<T|any>;