const cUtil = require("@ctrip/node-vampire-util");
const id = parseInt(process.env[process.env.instance_var || 'NODE_APP_INSTANCE'], 10) || 0

module.exports=(message,warn)=>{
    if (warn) {
        console.warn('\x1B[31m%s\x1B[0m', `[${cUtil.formatDateTime()}-${id} Vampire-Metric ]: ` + message + '\r\n');
    } else {
        console.log(`[${cUtil.formatDateTime()}-${id} Vampire-Metric ]: ` + message + '\r\n');
    }
};