import Metric from '../';

let testMetric = new Metric({
    name: 'nodejs.ctriputil.test.metric',
    tags: {
        'type': 'AAA',
        'level': '0'
    },
    interval: 60000
});

setInterval(function () {
    testMetric.add(Math.random(), {
        level: (Math.random() + '').slice(2, 3)
    });
}, 1000).unref();

testMetric.start();
