const re = /^(.*):([0-9a-zA-Z][0-9a-zA-Z\-_.]*)$/;

class CheckFormat {
    checkFiles(configName: string) {
        if (!re.test(configName)) {
            throw new Error(`configName: ${configName} is invalid`);
        }
        return {
            appid: RegExp.$1,
            configName: RegExp.$2
        };
    }
}

export default new CheckFormat();
