const assert = require('assert');
const singleton = require('../index');

describe('node-vampire-singleton UT', () => {

    it('找到对应的npm包', (done) => {
        const result = singleton('mocha')
        assert(!!result===true)
        done();
    });
    it('找不到指定的npm包', (done) => {
        const result = singleton('1111');
        assert(!result===true)
        done();
    });
});


