import {SoaWebApi, SoaAgent, Soa} from '../index';

new SoaWebApi({serviceCode: 16264, format: 'json'}, (err, datas) => {
    datas.invoke('hello', {}, (err, result) => {
        console.log(result)
    }, false, {}, {}, false)
});
const _soaAgent = SoaAgent(16264)
_soaAgent.invoke('hello', {}, false, {}, {}).then((datas) => {
    console.log('http://10.5.150.17/nodejs====',datas)
})
_soaAgent.clear().invoke('checkHealth', {}, false, {}, {}).then((datas) => {
    console.log('===>', datas)
})
