export interface tripLogMessage {
    type: string,
    title: string
    message: any,
    addInfo: object,
    scenario?: string | boolean,
    name?: string,
    source?: string,
    attributes?: object
}
interface _tripLog {
    /**triplog 对外接口方法 custom（传入type） */
    custom(data: tripLogMessage): void;
    /**
     * 
     * @param {string} key 全链路上下文传递;会跨链路传递到下游，影响后续整个链路的调用，使用前请考虑清楚，通常只用于 traceId , orderId 字段，慎用！！！
     * @param {string} value 
     */
    setTraceContext(key: string, value: any): void;
}

export const tripLog: _tripLog;
