'use strict'
const SOAClient = require('./soa-client')

class LoggingSOAClient extends SOAClient {
  constructor () {
    super({ serviceName: 'IBUPlatformSharkLogging', serviceCode: 15272 })
  }

  reportRecentUsedKey (requestBody) {
    return this.invoke('reportRecentUsedKey', requestBody)
  }

  reportKeyNotFound (requestBody) {
    return this.invoke('reportKeyNotFound', requestBody)
  }

  reportMetric (requestBody) {
    return this.invoke('reportMetric', requestBody)
  }
}

module.exports = new LoggingSOAClient()
