import cargoExported from '@ctrip/cargo-nodejs';

export { LOG_LEVEL, DATA_TYPE, CONTEXT_TYPE, middlewares } from '@ctrip/cargo-nodejs';

export {
  Channel,
  Currency,
  Group,
  Key,
  Locale,
  Site,
  Source,
  Data,
  ExtendedData,
} from '@ctrip/cargo-nodejs/lib/cache/cache-entry';

export default cargoExported;

export type CargoExport = typeof cargoExported;

export type CargoTrait = Omit<CargoExport, 'LOG_LEVEL' | 'DATA_TYPE' | 'CONTEXT_TYPE' | 'middlewares'>;

export type ICargoContext = ReturnType<CargoExport['getCargoContext']>;

export type IQueryBuilder = ReturnType<CargoExport['select']>;

export type IQueryOther = ReturnType<IQueryBuilder['other']>;

export type IQuery = ReturnType<IQueryOther['candidate']>;
